            gs.error(message);
        }
    }

})();

function fetchProp() {
    try {
        var inputs = {};
        inputs['property'] = 'x_58872_needit.autoCloseOverdue'; // String 
        // Execute Synchronously: Run in foreground. Code snippet has access to outputs.
        var result = sn_fd.FlowAPI.getRunner().action('x_33947_needit_spo.get_property').inForeground().withInputs(inputs).run();
        var outputs = result.getOutputs();
        var variable = outputs['variable']; // String
        return variable;
    } catch (ex) {
        var message = ex.getMessage();
        gs.error(message);
    }
}

function fetchDate() {
    var val = fetchProp();
    try {
        var inputs = {};
        inputs['days_ago'] = val; // Integer 
        // Execute Synchronously: Run in foreground. Code snippet has access to outputs.
        var result = sn_fd.FlowAPI.getRunner().action('x_33947_needit_spo.get_days_ago_date').inForeground().withInputs(inputs).run();
        var outputs = result.getOutputs();
        var old_date = outputs['old_date']; // Date
        return old_date;
    } catch (ex) {
        var message = ex.getMessage();
        gs.error(message);
    }
}
